src/contexts/AuthContext.jsx.content:
import React, { createContext, useContext, useState, useEffect } from 'react'
import { authenticateUser, authenticateUserOrSupplier, registerUser } from '../data/userDatabase'

const AuthContext = createContext()

export const useAuth = () => {
  const context = useContext(AuthContext)
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider')
  }
  return context
}

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null)
  const [loading, setLoading] = useState(true)
  const [userType, setUserType] = useState(null) // 'vendor' or 'supplier'

  useEffect(() => {
    // Check for stored user data on app load
    const storedUser = localStorage.getItem('vendorMitraUser')
    const storedUserType = localStorage.getItem('vendorMitraUserType')

    if (storedUser) {
      setUser(JSON.parse(storedUser))
      setUserType(storedUserType)
    }
    setLoading(false)
  }, [])

  const login = async (email, password) => {
    try {
      // Use the enhanced authentication that checks both users and suppliers
      const authenticatedUser = authenticateUserOrSupplier(email, password)
      if (authenticatedUser) {
        setUser(authenticatedUser)
        setUserType(authenticatedUser.userType)
        localStorage.setItem('vendorMitraUser', JSON.stringify(authenticatedUser))
        localStorage.setItem('vendorMitraUserType', authenticatedUser.userType)
        return { success: true }
      } else {
        throw new Error('Invalid credentials')
      }
    } catch (error) {
      return { success: false, error: error.message }
    }
  }

  const register = async (userData) => {
    try {
      // Use the database registration
      const newUser = registerUser(userData)
      setUser(newUser)
      setUserType(newUser.userType)
      localStorage.setItem('vendorMitraUser', JSON.stringify(newUser))
      localStorage.setItem('vendorMitraUserType', newUser.userType)
      return { success: true }
    } catch (error) {
      return { success: false, error: error.message }
    }
  }

  const logout = () => {
    setUser(null)
    setUserType(null)
    localStorage.removeItem('vendorMitraUser')
    localStorage.removeItem('vendorMitraUserType')
    // Force page reload to clear any cached state
    window.location.href = '/'
  }

  const updateProfile = (updates) => {
    const updatedUser = { ...user, ...updates }
    setUser(updatedUser)
    localStorage.setItem('vendorMitraUser', JSON.stringify(updatedUser))
  }

  const value = {
    user,
    userType,
    loading,
    login,
    register,
    logout,
    updateProfile
  }

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  )
}

src/contexts/CartContext.jsx.content:
import React, { createContext, useContext, useState } from 'react'

const CartContext = createContext()

export const useCart = () => {
  const context = useContext(CartContext)
  if (!context) {
    throw new Error('useCart must be used within a CartProvider')
  }
  console.log('useCart hook called, cart state:', context.cart)
  return context
}

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([])

  const addToCart = (item) => {
    console.log('CartContext: Adding item to cart:', item)
    setCart(prevCart => {
      const existingItem = prevCart.find(cartItem => cartItem.id === item.id)
      if (existingItem) {
        const newCart = prevCart.map(cartItem =>
          cartItem.id === item.id
            ? { ...cartItem, quantity: cartItem.quantity + (item.quantity || 1) }
            : cartItem
        )
        console.log('CartContext: Updated existing item, new cart:', newCart)
        return newCart
      }
      const newCart = [...prevCart, { ...item, quantity: item.quantity || 1 }]
      console.log('CartContext: Added new item, new cart:', newCart)
      return newCart
    })
  }

  const removeFromCart = (itemId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== itemId))
  }

  const updateQuantity = (itemId, quantity) => {
    if (quantity <= 0) {
      removeFromCart(itemId)
      return
    }
    setCart(prevCart =>
      prevCart.map(item =>
        item.id === itemId ? { ...item, quantity } : item
      )
    )
  }

  const clearCart = () => {
    setCart([])
  }

  const getCartTotal = () => {
    return cart.reduce((total, item) => {
      const price = typeof item.price === 'string' 
        ? parseInt(item.price.replace(/[^\d]/g, '')) 
        : item.price
      return total + (price * item.quantity)
    }, 0)
  }

  const getCartCount = () => {
    return cart.reduce((count, item) => count + item.quantity, 0)
  }

  const value = {
    cart,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    getCartTotal,
    getCartCount
  }

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  )
}

src/contexts/LanguageContext.jsx.content:
import React, { createContext, useContext, useState, useEffect } from 'react'

const LanguageContext = createContext()

export const useLanguage = () => {
  const context = useContext(LanguageContext)
  if (!context) {
    throw new Error('useLanguage must be used within a LanguageProvider')
  }
  return context
}

export const LanguageProvider = ({ children }) => {
  const [currentLanguage, setCurrentLanguage] = useState('en')
  const [translations, setTranslations] = useState({})

  const languages = {
    en: 'English',
    hi: 'हिंदी',
    ta: 'தமிழ்',
    te: 'తెలుగు',
    bn: 'বাংলা'
  }

  useEffect(() => {
    // Load translations based on current language
    import(`../i18n/${currentLanguage}.js`)
      .then(module => setTranslations(module.default))
      .catch(() => setTranslations({}))
  }, [currentLanguage])

  const t = (key) => {
    return translations[key] || key
  }

  const changeLanguage = (lang) => {
    setCurrentLanguage(lang)
    localStorage.setItem('vendorMitraLanguage', lang)
  }

  useEffect(() => {
    const savedLanguage = localStorage.getItem('vendorMitraLanguage')
    if (savedLanguage) {
      setCurrentLanguage(savedLanguage)
    }
  }, [])

  const value = {
    currentLanguage,
    languages,
    t,
    changeLanguage
  }

  return (
    <LanguageContext.Provider value={value}>
      {children}
    </LanguageContext.Provider>
  )
}

