import React, { useState, useEffect, useCallback } from 'react'
import { useNavigate } from 'react-router-dom'
import { useAuth } from '../contexts/AuthContext'
import { useCart } from '../contexts/CartContext'
import { Zap, ShoppingCart, Clock } from 'lucide-react'
import { flashSalesDatabase } from '../data/userDatabase'
import realTimeSync from '../utils/realTimeSync'
import FlashSaleTimer from '../components/FlashSales/FlashSaleTimer'
import BuyNowDialog from '../components/BuyNow/BuyNowDialog'
import QuantitySelector from '../components/Cart/QuantitySelector'

const FindSales = () => {
  const navigate = useNavigate()
  const { user } = useAuth()
  const { addToCart } = useCart()
  const [flashSales, setFlashSales] = useState([])
  const [expiredSales, setExpiredSales] = useState(new Set())
  const [showBuyNowDialog, setShowBuyNowDialog] = useState(false)
  const [buyNowItem, setBuyNowItem] = useState(null)
  const [showQuantitySelector, setShowQuantitySelector] = useState(false)
  const [selectedFlashSale, setSelectedFlashSale] = useState(null)

  const loadFlashSales = useCallback(() => {
    const sales = flashSalesDatabase.getAllFlashSales()
    const currentTime = new Date()

    // Filter out expired sales
    const validSales = sales.filter(sale => {
      const endTime = new Date(sale.endTime)
      return currentTime < endTime
    })
    setFlashSales(validSales)
  }, [])

  // Handle timer expiration
  const handleTimerExpired = useCallback((saleId) => {
    setExpiredSales(prev => new Set([...prev, saleId]))
    // Remove expired sale after a short delay to show "EXPIRED" state
    setTimeout(() => {
      const expiredSale = flashSales.find(sale => sale.id === saleId)
      if (expiredSale && expiredSale.total > expiredSale.sold) {
        // Move remaining stock back to normal product catalog
        const remainingStock = expiredSale.total - expiredSale.sold
        // Add back to product database with original price
        const productData = {
          name: expiredSale.product,
          price: expiredSale.oldPrice || expiredSale.price,
          quantity: remainingStock,
          unit: 'kg', // Default unit
          description: `${expiredSale.product} - Flash sale ended`,
          image: expiredSale.image,
          isOrganic: false,
          supplier: expiredSale.supplier,
          supplierId: expiredSale.supplierId,
          category: 'general'
        }
        // Import productDatabase to add the product back
        import('../data/userDatabase').then(({ productDatabase }) => {
          productDatabase.addProduct(productData)
        })
      }

      setFlashSales(prev => prev.filter(sale => sale.id !== saleId))
      setExpiredSales(prev => {
        const newSet = new Set(prev)
        newSet.delete(saleId)
        return newSet
      })
      // Also remove from database
      flashSalesDatabase.deleteFlashSale(saleId)
      // Emit real-time sync
      realTimeSync.emit('flash_sale_update', {
        action: 'expired_remove',
        saleId: saleId,
        remainingStock: expiredSale ? expiredSale.total - expiredSale.sold : 0
      })
    }, 2000)
  }, [flashSales])

  useEffect(() => {
    loadFlashSales()

    // Subscribe to real-time flash sale updates
    const unsubscribe = realTimeSync.subscribe('flash_sale_update', (data) => {
      if (data && data.action) {
        loadFlashSales()
      }
    })

    return unsubscribe
  }, [loadFlashSales])

  const handleAddToCart = (flashSale) => {
    if (!user) {
      alert('Please log in to add items to cart')
      navigate('/login')
      return
    }

    if (flashSale.sold >= flashSale.total) {
      alert('This flash sale is sold out!')
      return
    }

    // Double check remaining stock
    const remainingStock = flashSale.remainingStock !== undefined ? flashSale.remainingStock : (flashSale.total - flashSale.sold)
    if (remainingStock <= 0) {
      alert('This flash sale is sold out!')
      return
    }

    setSelectedFlashSale(flashSale)
    setShowQuantitySelector(true)
  }

  const handleQuantityConfirm = (quantity) => {
    const cartItem = {
      id: selectedFlashSale.id,
      name: selectedFlashSale.product,
      price: selectedFlashSale.price,
      image: selectedFlashSale.image,
      supplier: selectedFlashSale.supplier,
      supplierId: selectedFlashSale.supplierId,
      quantity: quantity,
      isFlashSale: true,
      unit: selectedFlashSale.quantityUnit || 'piece'
    }

    addToCart(cartItem)

    // Don't update sold count when adding to cart - only when purchasing
    realTimeSync.emit('flash_sale_update', {
      action: 'add_to_cart',
      flashSale: selectedFlashSale,
      supplierId: selectedFlashSale.supplierId
    })

    alert(`${quantity} ${selectedFlashSale.quantityUnit || 'piece'}${quantity > 1 ? 's' : ''} of ${selectedFlashSale.product} added to cart!`)
  }

  const handleBuyNow = (flashSale) => {
    if (!user) {
      alert('Please log in to make a purchase')
      navigate('/login')
      return
    }

    if (flashSale.sold >= flashSale.total) {
      alert('This flash sale is sold out!')
      return
    }

    // Double check remaining stock
    const remainingStock = flashSale.remainingStock !== undefined ? flashSale.remainingStock : (flashSale.total - flashSale.sold)
    if (remainingStock <= 0) {
      alert('This flash sale is sold out!')
      return
    }

    setBuyNowItem({
      ...flashSale,
      name: flashSale.product,
      supplier: flashSale.supplier,
      unit: flashSale.quantityUnit || 'piece',
      quantity: 1,
      totalPrice: flashSale.price
    })
    setShowBuyNowDialog(true)
  }

  const handleConfirmOrder = async (orderData) => {
    const { item, quantity, totalPrice, deliveryAddress, paymentMethod } = orderData

    // Use proper flash sale inventory management
    const updatedSale = flashSalesDatabase.decreaseFlashSaleStock(item.id, quantity)

    if (!updatedSale) {
      alert('Failed to update flash sale inventory!')
      return
    }

    // Create delivery record
    import('../data/userDatabase').then(({ deliveriesDatabase }) => {
      deliveriesDatabase.addDelivery({
        customer: user?.name || 'Customer',
        customerId: user?.id || 1,
        supplier: item.supplier,
        supplierId: item.supplierId,
        products: [`${quantity} piece of ${item.product} (Flash Sale)`],
        totalAmount: totalPrice,
        status: 'in-transit',
        deliveryDate: new Date(Date.now() + 2 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
        deliveryAddress,
        paymentMethod
      })
    })

    realTimeSync.emit('flash_sale_update', {
      action: 'purchase',
      flashSale: updatedSale,
      supplierId: item.supplierId
    })

    loadFlashSales()
    alert(`Order placed successfully! Total: ₹${totalPrice}`)
  }

  return (
    <div className="max-w-7xl mx-auto py-12 px-4">
      <div className="flex items-center gap-3 mb-8">
        <Zap className="w-8 h-8 text-orange-600" />
        <h1 className="text-3xl font-bold text-orange-600">Flash Sales</h1>
      </div>

      {flashSales.length === 0 ? (
        <div className="text-center py-12">
          <Zap className="w-16 h-16 text-gray-400 mx-auto mb-4" />
          <h3 className="text-xl font-semibold text-gray-600 mb-2">No Flash Sales Available</h3>
          <p className="text-gray-500">Check back later for exciting deals!</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {flashSales.map((sale) => {
            const isExpired = expiredSales.has(sale.id)
            const remainingStock = sale.remainingStock !== undefined ? sale.remainingStock : (sale.total - sale.sold)
            const isSoldOut = remainingStock <= 0

            return (
              <div 
                key={sale.id} 
                className={`bg-white rounded-lg shadow-md overflow-hidden border transition-all duration-300 ${
                  isSoldOut || isExpired ? 'opacity-60 bg-gray-100' : 'hover:shadow-lg'
                }`}
              >
                <div className="relative">
                  <img 
                    src={sale.image} 
                    alt={sale.product} 
                    className="w-full h-48 object-cover"
                  />
                  <div className="absolute top-2 right-2 bg-red-500 text-white px-2 py-1 rounded text-sm font-bold">
                    {sale.discount}% OFF
                  </div>
                  {isExpired && (
                    <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
                      <span className="text-white text-2xl font-bold">EXPIRED</span>
                    </div>
                  )}
                  {isSoldOut && !isExpired && (
                    <div className="absolute inset-0 bg-red-500 bg-opacity-75 flex items-center justify-center">
                      <span className="text-white text-2xl font-bold">SOLD OUT</span>
                    </div>
                  )}
                </div>

                <div className="p-4">
                  <h3 className="text-lg font-semibold mb-2">{sale.product}</h3>
                  <div className="flex items-center justify-between mb-2">
                    <div className="flex items-center gap-2">
                      <span className="text-2xl font-bold text-green-600">₹{sale.price}</span>
                      <span className="text-sm text-gray-500 line-through">₹{sale.oldPrice}</span>
                      <span className="text-xs text-gray-500">per {sale.quantityUnit || 'piece'}</span>
                    </div>
                  </div>

                  <p className="text-sm text-gray-600 mb-2">by {sale.supplier}</p>

                  <div className="flex items-center justify-between mb-3">
                    <span className="text-sm text-gray-600">
                      Sold: {sale.sold}/{sale.total} per {sale.quantityUnit || 'piece'}
                    </span>
                    <div className="flex items-center gap-1 text-red-500">
                      <Clock className="w-4 h-4" />
                      <FlashSaleTimer 
                        endTime={sale.endTime} 
                        onExpired={() => handleTimerExpired(sale.id)}
                      />
                    </div>
                  </div>

                  <div className="flex gap-2">
                    <button
                      onClick={() => handleAddToCart(sale)}
                      disabled={isSoldOut || isExpired}
                      className="flex-1 bg-blue-600 text-white py-2 px-4 rounded-lg font-medium hover:bg-blue-700 disabled:bg-gray-300 disabled:cursor-not-allowed transition-colors flex items-center justify-center gap-2"
                    >
                      <ShoppingCart className="w-4 h-4" />
                      {isSoldOut ? 'Sold Out' : 'Add to Cart'}
                    </button>
                    <button
                      onClick={() => handleBuyNow(sale)}
                      disabled={isSoldOut || isExpired}
                      className="flex-1 bg-green-600 text-white py-2 px-4 rounded-lg font-medium hover:bg-green-700 disabled:bg-gray-300 disabled:cursor-not-allowed transition-colors"
                    >
                      Buy Now
                    </button>
                  </div>
                </div>
              </div>
            )
          })}
        </div>
      )}

      {showBuyNowDialog && buyNowItem && (
        <BuyNowDialog
          isOpen={showBuyNowDialog}
          onClose={() => {
            setShowBuyNowDialog(false)
            setBuyNowItem(null)
          }}
          item={buyNowItem}
        />
      )}

      {showQuantitySelector && selectedFlashSale && (
        <QuantitySelector
          isOpen={showQuantitySelector}
          onClose={() => {
            setShowQuantitySelector(false)
            setSelectedFlashSale(null)
          }}
          item={selectedFlashSale}
          onConfirm={handleQuantityConfirm}
        />
      )}
    </div>
  )
}

export default FindSales

src/pages/Organic.jsx.content:
import React, { useState, useEffect } from 'react'
import { useCart } from '../contexts/CartContext'
import { useProducts } from '../contexts/ProductContext'
import { Search, Filter, Store, Star, MapPin, MessageSquare } from 'lucide-react'
import realTimeSync from '../utils/realTimeSync'
import Negotiation from '../components/Bargain/Negotiation'
import ReviewSystem from '../components/Rating/ReviewSystem'
const Organic = () => {
  const { addToCart } = useCart()
  const { products: allProducts, loading: productsLoading } = useProducts()
  const [search, setSearch] = useState('')
  const [sortBy, setSortBy] = useState('name')
  const [showFilters, setShowFilters] = useState(false)
  const [selectedProduct, setSelectedProduct] = useState(null)
  const [showNegotiation, setShowNegotiation] = useState(false)
  const [selectedProductForReview, setSelectedProductForReview] = useState(null)
  const [showReviews, setShowReviews] = useState(false)
  const [minPrice, setMinPrice] = useState(0)
  const [maxPrice, setMaxPrice] = useState(200)
  const [minRating, setMinRating] = useState(0)
  const [organicOnly, setOrganicOnly] = useState(false)
  const [fssaiOnly, setFssaiOnly] = useState(false)
  const [products, setProducts] = useState([])

  useEffect(() => {
    const loadOrganicProducts = () => {
      try {
        console.log('All products for organic page:', allProducts)
        if (allProducts && allProducts.length > 0) {
          const organicProducts = allProducts
            .filter(product => product.isOrganic === true || product.organic === true)
            .map(product => ({
              id: product.id,
              name: product.name,
              price: product.price,
              image: product.image,
              supplier: product.supplierName,
              supplierId: product.supplierId,
              description: product.description || `Fresh organic ${product.name} from ${product.supplierName}`,
              rating: product.supplierRating || 4.5,
              reviews: Math.floor(Math.random() * 50) + 10,
              quantity: `${product.stock || product.quantity || 0} ${product.unit || 'kg'}`,
              availableStock: product.stock || product.quantity || 0,
              unit: product.unit || 'kg',
              certifications: ['Organic', 'FSSAI']
            }))
          console.log('Filtered organic products:', organicProducts)
          setProducts(organicProducts)
        } else {
          setProducts([])
        }
      } catch (error) {
        console.error('Error loading organic products:', error)
        setProducts([])
      }
    }

    loadOrganicProducts()

    // Subscribe to product updates for real-time sync
    const unsubscribe = realTimeSync.subscribe('product_update', (data) => {
      console.log('Product update received in Organic page:', data)
      if (data && data.action) {
        setTimeout(() => {
          loadOrganicProducts()
        }, 200)
      }
    })

    return () => unsubscribe()
  }, [allProducts])

  const filteredProducts = products.filter(product => {
    const nameMatches = product.name.toLowerCase().includes(search.toLowerCase()) ||
                       product.supplier.toLowerCase().includes(search.toLowerCase())
    const priceMatches = product.price >= minPrice && product.price <= maxPrice
    const ratingMatches = product.rating >= minRating
    const organicMatches = !organicOnly || product.certifications.includes('Organic')
    const fssaiMatches = !fssaiOnly || product.certifications.includes('FSSAI')

    return nameMatches && priceMatches && ratingMatches && organicMatches && fssaiMatches
  })

  const sortedProducts = [...filteredProducts].sort((a, b) => {
    switch (sortBy) {
      case 'price':
        return a.price - b.price
      case 'rating':
        return b.rating - a.rating
      case 'name':
        return a.name.localeCompare(b.name)
      default:
        return 0
    }
  })

  const startBargain = (product) => {
    setSelectedProduct(product)
    setShowNegotiation(true)
  }

  const buyNow = (product) => {
    // Check stock availability
    if (product.availableStock <= 0) {
      alert('This product is out of stock!')
      return
    }

    // Add product to cart instead of direct purchase
    const cartItem = {
      id: product.id,
      name: product.name,
      price: product.price,
      image: product.image,
      supplier: product.supplier,
      supplierId: product.supplierId,
      quantity: 1,
      unit: product.unit
    }
    addToCart(cartItem)
    alert(`${product.name} added to cart!`)
  }

  const handleReviewSubmit = (review) => {
    console.log('New review submitted:', review)
  }

  const totalFarmers = new Set(products.map(p => p.supplier)).size
  const organicProducts = products.filter(p => p.certifications.includes('Organic')).length
  const avgRating = products.length > 0 ? (products.reduce((sum, p) => sum + p.rating, 0) / products.length).toFixed(1) : '0'

  return (
    <div className="max-w-7xl mx-auto py-12 px-4">
      <div className="flex items-center gap-3 mb-8">
        <Store className="w-8 h-8 text-green-600" />
        <h1 className="text-3xl font-bold text-primary-600">Organic Products Marketplace</h1>
      </div>

      {/* Search Bar and Filters */}
      <div className="mb-6 space-y-4">
        <div className="flex flex-col md:flex-row gap-4">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
            <input
              type="text"
              placeholder="Search Organic Products..."
              value={search}
              onChange={e => setSearch(e.target.value)}
              className="input-field pl-10"
            />
          </div>
          <button
            onClick={() => setShowFilters(!showFilters)}
            className="btn-secondary flex items-center gap-2"
          >
            <Filter className="w-4 h-4" />
            Filter
          </button>
        </div>

        {showFilters && (
          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Min Price (₹/kg)
                </label>
                <input
                  type="number"
                  min="0"
                  max="200"
                  value={minPrice}
                  onChange={e => setMinPrice(Number(e.target.value))}
                  className="input-field"
                  placeholder="0"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Max Price (₹/kg)
                </label>
                <input
                  type="number"
                  min="0"
                  max="200"
                  value={maxPrice}
                  onChange={e => setMaxPrice(Number(e.target.value))}
                  className="input-field"
                  placeholder="200"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Min Rating
                </label>
                <select
                  value={minRating}
                  onChange={e => setMinRating(Number(e.target.value))}
                  className="input-field"
                >
                  <option value={0}>Any Rating</option>
                  <option value={3}>3+ Stars</option>
                  <option value={4}>4+ Stars</option>
                  <option value={4.5}>4.5+ Stars</option>
                </select>
              </div>
            </div>
          </div>
        )}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Product List */}
        <div className="lg:col-span-2">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {sortedProducts.map(product => {
              const isOutOfStock = product.availableStock <= 0
              return (
              <div key={product.id} className={`rounded-lg shadow-md overflow-hidden transition-shadow ${
                isOutOfStock 
                  ? 'bg-gray-100 opacity-60' 
                  : 'bg-white hover:shadow-lg'
              }`}>
                <img 
                  src={product.image} 
                  alt={product.name}
                  className={`w-full h-48 object-cover ${
                    isOutOfStock ? 'grayscale' : ''
                  }`}
                />
                <div className={`p-6 ${isOutOfStock ? 'text-gray-500' : ''}`}>
                  <div className="flex justify-between items-start mb-2">
                    <h3 className={`text-xl font-bold ${isOutOfStock ? 'text-gray-600' : ''}`}>{product.name}</h3>
                    <div className="text-right">
                      <div className={`text-2xl font-bold ${isOutOfStock ? 'text-gray-500' : 'text-green-600'}`}>₹{product.price}/{product.unit}</div>
                      <div className={`text-sm font-medium ${
                        product.availableStock <= 0 
                          ? 'text-red-600' 
                          : product.availableStock < 10 
                            ? 'text-orange-600' 
                            : 'text-gray-600'
                      }`}>
                        {product.availableStock <= 0 
                          ? 'Out of Stock' 
                          : `${product.availableStock} ${product.unit} available`
                        }
                      </div>
                    </div>
                  </div>

                  <p className={`mb-4 ${isOutOfStock ? 'text-gray-400' : 'text-gray-600'}`}>{product.description}</p>

                  {/* Out of Stock Notification */}
                  {isOutOfStock && (
                    <div className="mb-4 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                      <p className="text-sm text-blue-700 font-medium">
                        📧 We will notify you when this product is available
                      </p>
                    </div>
                  )}

                  <div className="flex items-center gap-2 mb-4">
                    <div className="flex items-center gap-1">
                      <Star className={`w-4 h-4 ${isOutOfStock ? 'text-gray-400' : 'text-yellow-400'}`} />
                      <span className={`text-sm ${isOutOfStock ? 'text-gray-400' : ''}`}>{product.rating}</span>
                      <span className={`text-sm ${isOutOfStock ? 'text-gray-400' : 'text-gray-500'}`}>({product.reviews})</span>
                    </div>
                    <div className={`text-sm ${isOutOfStock ? 'text-gray-400' : 'text-gray-500'}`}>
                      by {product.supplier}
                    </div>
                  </div>

                  <div className="flex flex-wrap gap-1 mb-4">
                    {product.certifications.map(cert => (
                      <span key={cert} className={`text-xs px-2 py-1 rounded ${
                        isOutOfStock 
                          ? 'bg-gray-200 text-gray-500' 
                          : 'bg-blue-100 text-blue-700'
                      }`}>
                        {cert}
                      </span>
                    ))}
                  </div>

                  <div className="flex gap-2">
                    <button 
                      className={`flex-1 ${product.availableStock <= 0 
                        ? 'bg-gray-300 text-gray-500 cursor-not-allowed' 
                        : 'btn-primary'}`}
                      onClick={() => buyNow(product)}
                      disabled={product.availableStock <= 0}
                    >
                      {product.availableStock <= 0 ? 'Out of Stock' : 'Add to Cart'}
                    </button>
                    <button 
                      className={`flex items-center gap-1 py-2 px-4 rounded-lg font-medium transition-colors ${
                        isOutOfStock 
                          ? 'bg-gray-200 text-gray-400 cursor-not-allowed' 
                          : 'bg-orange-100 text-orange-700 hover:bg-orange-200'
                      }`}
                      onClick={() => startBargain(product)}
                      disabled={product.availableStock <= 0}
                    >
                      <MessageSquare className="w-4 h-4" />
                      Bargain
                    </button>

                    <button 
                      className={`flex items-center gap-1 py-2 px-4 rounded-lg font-medium transition-colors ${
                        isOutOfStock 
                          ? 'bg-gray-200 text-gray-400 cursor-not-allowed' 
                          : 'bg-orange-100 text-orange-700 hover:bg-orange-200'
                      }`}
                      onClick={() => setSelectedProductForReview(product)}
                      disabled={isOutOfStock}
                    >
                      <Star className="w-4 h-4" />
                      Reviews
                    </button>
                  </div>
                </div>
              </div>
              )
            })}
          </div>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Negotiation Panel */}
          {showNegotiation && selectedProduct && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h3 className="text-lg font-semibold mb-4">
                Bargain with {selectedProduct.supplier}
              </h3>
              <Negotiation 
                initialOffer={selectedProduct.price} 
              />
            </div>
          )}
        </div>
      </div>

      {/* Review System Modal */}
      {selectedProductForReview && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4 max-h-[90vh] overflow-y-auto">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold">Reviews for {selectedProductForReview.name}</h3>
              <button
                onClick={() => setSelectedProductForReview(null)}
                className="text-gray-500 hover:text-gray-700"
              >
                ✕
              </button>
            </div>
            <ReviewSystem
              itemId={selectedProductForReview.id}
              itemName={selectedProductForReview.name}
              supplierId={selectedProductForReview.supplierId}
              supplierName={selectedProductForReview.supplier}
              onClose={() => setSelectedProductForReview(null)}
              onSubmit={handleReviewSubmit}
              readOnly={true}
            />
          </div>
        </div>
      )}
    </div>
  )
}

export default Organic

