import React, { useState, useEffect } from 'react'
import { useAuth } from '../contexts/AuthContext'
import { Shield, Save, Edit, Camera, MapPin, Phone, Mail, User, Award, Star, Package, Clock, MessageSquare } from 'lucide-react'
import LicenseVerification from '../components/FSSAI/LicenseVerification'
import { deliveriesDatabase, reviewsDatabase } from '../data/userDatabase'
import ReviewSystem from '../components/Rating/ReviewSystem'
import realTimeSync from '../utils/realTimeSync'

const Profile = () => {
  const { user, updateProfile } = useAuth()
  const [editing, setEditing] = useState(false)
  const [showFssaiVerification, setShowFssaiVerification] = useState(false)
  const [recentOrders, setRecentOrders] = useState([])
  const [selectedOrderForReview, setSelectedOrderForReview] = useState(null)
  const [selectedProductForReview, setSelectedProductForReview] = useState(null)
  const [profileData, setProfileData] = useState({
    name: user?.name || '',
    phone: user?.phone || '',
    address: user?.address || '',
    fssaiLicense: user?.fssaiLicense || '',
    businessName: user?.businessName || '',
    businessType: user?.businessType || '',
    description: user?.description || ''
  })

  // Load recent orders from database
  useEffect(() => {
    if (user?.id) {
      const orders = deliveriesDatabase.getDeliveriesByVendor(user.id)
      // Sort orders by creation date (newest first)
      const sortedOrders = orders.sort((a, b) => {
        const dateA = new Date(a.orderDate || a.id)
        const dateB = new Date(b.orderDate || b.id)
        return dateB - dateA
      })
      setRecentOrders(sortedOrders)
    }
  }, [user?.id])

  // Listen for real-time order and delivery updates
  useEffect(() => {
    if (!user?.id) return

    let refreshTimeout
    let lastRefreshTime = 0

    const refreshOrders = () => {
      const now = Date.now()
      // Prevent too frequent refreshes (max once per 200ms)
      if (now - lastRefreshTime < 200) {
        clearTimeout(refreshTimeout)
        refreshTimeout = setTimeout(refreshOrders, 200)
        return
      }

      lastRefreshTime = now
      const orders = deliveriesDatabase.getDeliveriesByVendor(user.id)
      const sortedOrders = orders.sort((a, b) => {
        const dateA = new Date(a.orderDate || a.id)
        const dateB = new Date(b.orderDate || b.id)
        return dateB - dateA
      })

      // Only update if orders actually changed
      setRecentOrders(prevOrders => {
        if (JSON.stringify(prevOrders) === JSON.stringify(sortedOrders)) {
          return prevOrders // No change, don't trigger re-render
        }
        return sortedOrders
      })
    }

    // Listen to stock updates for new purchases
    const unsubscribePurchase = realTimeSync.subscribe('stock_update', (data) => {
      if (data.action === 'purchase' && data.vendorId === user.id) {
        refreshOrders()
      }
    })

    // Listen for delivery status changes (like delivered status updates)
    const unsubscribeDelivery = realTimeSync.subscribe('delivery_update', (data) => {
      if (data.vendorId === user.id || data.customerId === user.id) {
        refreshOrders()
      }
    })

    return () => {
      clearTimeout(refreshTimeout)
      unsubscribePurchase()
      unsubscribeDelivery()
    }
  }, [user?.id])

  // Check if user has already reviewed a supplier
  const hasReviewedSupplier = (supplierId) => {
    if (!user?.id) return false
    const existingReviews = reviewsDatabase.getReviewsBySupplier(supplierId)
    return existingReviews.some(review => review.vendorId === user.id)
  }

  if (!user) {
    return (
      <div className="flex items-center justify-center min-h-[60vh]">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">Profile</h2>
          <p className="text-gray-600">Please log in to view your profile.</p>
        </div>
      </div>
    )
  }

  const handleSave = () => {
    updateProfile(profileData)
    setEditing(false)
  }

  const handleReviewSubmit = (review) => {
    console.log('Review submitted:', review)
    // Refresh orders to update review status
    if (user?.id) {
      const orders = deliveriesDatabase.getDeliveriesByVendor(user.id)
      const sortedOrders = orders.sort((a, b) => {
        const dateA = new Date(a.orderDate || a.id)
        const dateB = new Date(b.orderDate || b.id)
        return dateB - dateA
      })
      setRecentOrders(sortedOrders)
    }
    setSelectedOrderForReview(null)
    setSelectedProductForReview(null)
  }

  const handleProductReviewSubmit = (review) => {
    console.log('Product review submitted:', review)
    // Refresh orders to update review status
    if (user?.id) {
      const orders = deliveriesDatabase.getDeliveriesByVendor(user.id)
      const sortedOrders = orders.sort((a, b) => {
        const dateA = new Date(a.orderDate || a.id)
        const dateB = new Date(b.orderDate || b.id)
        return dateB - dateA
      })
      setRecentOrders(sortedOrders)
    }
    setSelectedProductForReview(null)
  }

  const handleInputChange = (field, value) => {
    setProfileData(prev => ({
      ...prev,
      [field]: value
    }))
  }

  return (
    <div className="max-w-4xl mx-auto py-12 px-4">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">Profile</h1>
        <button
          onClick={() => setEditing(!editing)}
          className="btn-secondary flex items-center gap-2"
        >
          {editing ? 'Cancel' : <Edit className="w-4 h-4" />}
          {editing ? '' : 'Edit Profile'}
        </button>
      </div>

      <div className="space-y-6">
        {/* Profile Information */}
        <div className="space-y-6">
          {/* Basic Info Card */}
          <div className="card">
            <h3 className="text-xl font-semibold mb-4 flex items-center gap-2">
              <User className="w-5 h-5" />
              Basic Information
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Name
                </label>
                {editing ? (
                  <input
                    type="text"
                    value={profileData.name}
                    onChange={(e) => handleInputChange('name', e.target.value)}
                    className="input-field"
                  />
                ) : (
                  <div className="text-gray-900">{user.name}</div>
                )}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Email
                </label>
                <div className="text-gray-900">{user.email}</div>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Phone
                </label>
                {editing ? (
                  <input
                    type="tel"
                    value={profileData.phone}
                    onChange={(e) => handleInputChange('phone', e.target.value)}
                    className="input-field"
                  />
                ) : (
                  <div className="text-gray-900">{user.phone || 'Not provided'}</div>
                )}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Address
                </label>
                {editing ? (
                  <textarea
                    value={profileData.address}
                    onChange={(e) => handleInputChange('address', e.target.value)}
                    className="input-field"
                    rows="3"
                  />
                ) : (
                  <div className="text-gray-900">{user.address || 'Not provided'}</div>
                )}
              </div>
            </div>
          </div>

          {/* Business Information */}
          {user.type === 'supplier' && (
            <div className="card">
              <h3 className="text-xl font-semibold mb-4 flex items-center gap-2">
                <Award className="w-5 h-5" />
                Business Information
              </h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Business Name
                  </label>
                  {editing ? (
                    <input
                      type="text"
                      value={profileData.businessName}
                      onChange={(e) => handleInputChange('businessName', e.target.value)}
                      className="input-field"
                    />
                  ) : (
                    <div className="text-gray-900">{user.businessName || 'Not provided'}</div>
                  )}
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Business Type
                  </label>
                  {editing ? (
                    <select
                      value={profileData.businessType}
                      onChange={(e) => handleInputChange('businessType', e.target.value)}
                      className="input-field"
                    >
                      <option value="">Select Type</option>
                      <option value="wholesale">Wholesale</option>
                      <option value="retail">Retail</option>
                      <option value="manufacturing">Manufacturing</option>
                      <option value="distribution">Distribution</option>
                    </select>
                  ) : (
                    <div className="text-gray-900">{user.businessType || 'Not specified'}</div>
                  )}
                </div>
                <div className="md:col-span-2">
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Business Description
                  </label>
                  {editing ? (
                    <textarea
                      value={profileData.description}
                      onChange={(e) => handleInputChange('description', e.target.value)}
                      className="input-field"
                      rows="3"
                    />
                  ) : (
                    <div className="text-gray-900">{user.description || 'No description'}</div>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* FSSAI Verification removed as per request */}

          {editing && (
            <div className="flex gap-4">
              <button onClick={handleSave} className="btn-primary flex items-center gap-2">
                <Save className="w-4 h-4" />
                Save
              </button>
              <button onClick={() => setEditing(false)} className="btn-secondary">
                Cancel
              </button>
            </div>
          )}
        </div>

        {/* Recent Orders Section */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
            <Package className="w-5 h-5 text-blue-600" />
            Recent Orders
          </h3>
          <div className="space-y-4">
            {recentOrders.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                <Package className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                <p>No orders yet</p>
                <p className="text-sm">Your recent orders will appear here</p>
              </div>
            ) : (
              recentOrders.slice(0, 5).map((order, orderIndex) => (
                <div key={`${order.id}-${order.orderDate}-${orderIndex}`} className="border rounded-lg p-4">
                  <div className="flex justify-between items-start mb-3">
                    <div className="flex-1">
                      <h4 className="font-medium text-gray-900 mb-1">
                        Order #{order.orderId || order.id}
                      </h4>
                      <p className="text-sm text-gray-600 mb-2">from {order.supplier}</p>

                      {/* Products List */}
                      <div className="space-y-3">
                        {order.products && Array.isArray(order.products) ? (
                          order.products.map((product, index) => {
                            const productName = typeof product === 'string' ? product : product.name || 'Item'
                            const productQuantity = typeof product === 'object' ? product.quantity || 1 : 1
                            const productUnit = typeof product === 'object' ? product.unit || 'kg' : 'kg'
                            const productPrice = typeof product === 'object' ? product.price : null
                            const productImage = typeof product === 'object' ? product.image : null

                            return (
                              <div key={`${order.id}-product-${index}-${productName}`} className="flex items-center gap-3 p-2 bg-gray-50 rounded-lg">
                                {/* Product Image */}
                                <div className="w-12 h-12 bg-gray-200 rounded-lg flex items-center justify-center overflow-hidden">
                                  {productImage ? (
                                    <img 
                                      src={productImage} 
                                      alt={productName}
                                      className="w-full h-full object-cover"
                                      onError={(e) => {
                                        e.target.style.display = 'none'
                                        e.target.nextSibling.style.display = 'flex'
                                      }}
                                    />
                                  ) : null}
                                  <div className="w-full h-12 bg-gray-300 flex items-center justify-center text-lg text-gray-500" style={{display: productImage ? 'none' : 'flex'}}>
                                    📦
                                  </div>
                                </div>

                                {/* Product Details */}
                                <div className="flex-1">
                                  <div className="flex items-center justify-between">
                                    <span className="text-sm font-medium text-gray-900">
                                      {productQuantity} {productUnit} {productName}
                                    </span>
                                    {productPrice && (
                                      <span className="text-sm text-gray-600">₹{productPrice}</span>
                                    )}
                                  </div>

                                  {/* Write Review Button for delivered orders */}
                                  {order.status === 'delivered' && !hasReviewedSupplier(order.supplierId) && (
                                    <button
                                      onClick={() => setSelectedProductForReview({
                                        ...product,
                                        orderId: order.orderId || order.id,
                                        supplierId: order.supplierId,
                                        supplierName: order.supplier
                                      })}
                                      className="text-sm bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded-md font-medium mt-2 transition-colors"
                                    >
                                      ✍️ Write Review
                                    </button>
                                  )}
                                </div>
                              </div>
                            )
                          })
                        ) : (
                          <div className="text-sm text-gray-700">
                            {order.products || 'Order Items'}
                          </div>
                        )}
                      </div>
                    </div>
                    <div className="text-right ml-4">
                      <div className="text-sm font-medium text-gray-900">
                        ₹{order.totalAmount}
                      </div>
                      <div className={`text-xs px-2 py-1 rounded-full ${
                        order.status === 'delivered' 
                          ? 'bg-green-100 text-green-800' 
                          : order.status === 'in-transit'
                          ? 'bg-blue-100 text-blue-800'
                          : 'bg-yellow-100 text-yellow-800'
                      }`}>
                        {order.status}
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center justify-between text-sm text-gray-500 mb-3">
                    <span>{new Date(order.orderDate).toLocaleDateString()}</span>
                    <span>Order ID: {order.orderId || order.id}</span>
                  </div>

                </div>
              ))
            )}
          </div>
        </div>

      </div>

      {/* Review Modal for Individual Products */}
      {selectedProductForReview && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4 max-h-[90vh] overflow-y-auto">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold">
                Review {selectedProductForReview.name}
              </h3>
              <button
                onClick={() => setSelectedProductForReview(null)}
                className="text-gray-500 hover:text-gray-700"
              >
                ✕
              </button>
            </div>
            <ReviewSystem
              itemId={selectedProductForReview.id}
              itemName={selectedProductForReview.name}
              supplierId={selectedProductForReview.supplierId}
              supplierName={selectedProductForReview.supplierName}
              onClose={() => setSelectedProductForReview(null)}
              onSubmit={handleProductReviewSubmit}
              readOnly={false}
            />
          </div>
        </div>
      )}

      {/* FSSAI Verification Modal */}
      {showFssaiVerification && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
            <h3 className="text-lg font-semibold mb-4">FSSAI License Verification</h3>
            <LicenseVerification 
              onVerify={(license) => {
                updateProfile({ fssaiLicense: license, fssaiVerified: true })
                setShowFssaiVerification(false)
              }}
            />
            <button
              onClick={() => setShowFssaiVerification(false)}
              className="w-full btn-secondary mt-4"
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  )
}

export default Profile

src/pages/SupplierDashboard.jsx.content:
import React, { useState, useEffect } from 'react'
import { useAuth } from '../contexts/AuthContext'
import { useProducts } from '../contexts/ProductContext'
import { useSearchParams } from 'react-router-dom'
import { flashSalesDatabase, reviewsDatabase, bargainsDatabase, deliveriesDatabase } from '../data/userDatabase'
import ImageUpload from '../components/ImageUpload'
import FlashSaleTimer from '../components/FlashSales/FlashSaleTimer'
import productApi from '../services/productApi'
import realTimeSync from '../utils/realTimeSync'
import { 
  Package, 
  Shield, 
  MessageSquare, 
  Zap, 
  Star, 
  Truck, 
  BarChart3, 
  Plus, 
  Edit, 
  Trash2, 
  CheckCircle, 
  XCircle,
  Users,
  TrendingUp,
  DollarSign,
  ShoppingCart,
  AlertCircle,
  Save,
  X
} from 'lucide-react'

export default function SupplierDashboard() {
  const { user } = useAuth()
  const { products: allProducts, addProduct, updateProduct, deleteProduct, getProductsBySupplier, loading: productsLoading } = useProducts()
  const [searchParams, setSearchParams] = useSearchParams()
  const [activeTab, setActiveTab] = useState('overview')
  const [products, setProducts] = useState([])
  const [bargains, setBargains] = useState([])
  const [reviews, setReviews] = useState([])
  const [deliveries, setDeliveries] = useState([])
  const [flashSales, setFlashSales] = useState([])
  const [finalizingOrderId, setFinalizingOrderId] = useState(null)
  const [showAddProduct, setShowAddProduct] = useState(false)
  const [showAddFlashSale, setShowAddFlashSale] = useState(false)
  const [editingProduct, setEditingProduct] = useState(null)
  const [editingFlashSale, setEditingFlashSale] = useState(null)
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    quantity: '',
    unit: 'kg',
    description: '',
    image: '',
    isOrganic: false,
    licenseNumber: '',
    isVerified: false,
    category: 'vegetables'
  })


  const [newFlashSale, setNewFlashSale] = useState({
    product: '',
    oldPrice: '',
    newPrice: '',
    discount: '',
    totalQuantity: '',
    quantityUnit: 'kg',
    durationDays: '',
    durationHours: '',
    durationMinutes: '',
    image: ''
  })

  const [selectedBargain, setSelectedBargain] = useState(null)
  const [bargainMessage, setBargainMessage] = useState('')
  const [bargainOffer, setBargainOffer] = useState('')

  // Handle URL parameters for tab navigation
  useEffect(() => {
    const tabParam = searchParams.get('tab')
    if (tabParam && ['overview', 'products', 'bargains', 'flashsales', 'reviews', 'deliveries'].includes(tabParam)) {
      setActiveTab(tabParam)
    }
  }, [searchParams])


  // Load data with real-time sync
  useEffect(() => {
    if (user && user.id) {
      const loadData = () => {
        try {
          const userBargains = bargainsDatabase.getBargainsBySupplier(user.id)
          setBargains(userBargains)

          const userReviews = reviewsDatabase.getReviewsBySupplier(user.id)
          setReviews(userReviews)

          const userDeliveries = deliveriesDatabase.getDeliveriesBySupplier(user.id)
          setDeliveries(userDeliveries)

          const userFlashSales = flashSalesDatabase.getFlashSalesBySupplier(user.id)
          setFlashSales(userFlashSales)
        } catch (error) {
          console.error('Error loading supplier data:', error)
        }
      }

      loadData()

      // Subscribe to real-time updates
      const unsubscribeBargain = realTimeSync.subscribe('bargain_update', (data) => {
        if (data && data.supplierId === user.id) {
          const userBargains = bargainsDatabase.getBargainsBySupplier(user.id)
          setBargains(userBargains)

          // Update selected bargain if it's currently open
          if (selectedBargain && data.bargain && selectedBargain.id === data.bargain.id) {
            setSelectedBargain(data.bargain)
          }
        }
      })

      const unsubscribeReview = realTimeSync.subscribe('review_update', (data) => {
        if (data && data.supplierId === user.id) {
          const userReviews = reviewsDatabase.getReviewsBySupplier(user.id)
          setReviews(userReviews)
        }
      })

      const unsubscribeFlashSale = realTimeSync.subscribe('flash_sale_update', (data) => {
        if (data && data.supplierId === user.id) {
          const userFlashSales = flashSalesDatabase.getFlashSalesBySupplier(user.id)
          setFlashSales(userFlashSales)
        }
      })

      return () => {
        unsubscribeBargain()
        unsubscribeReview()
        unsubscribeFlashSale()
      }
    }
  }, [user])

  // Load products from ProductContext - only when allProducts changes
  useEffect(() => {
    if (user && user.id) {
      const userProducts = getProductsBySupplier(user.id)
      console.log('Loading products for supplier from context:', user.id, userProducts)

      // Remove duplicates by ID before setting
      const uniqueProducts = userProducts.filter((product, index, self) => 
        index === self.findIndex(p => p.id === product.id)
      )

      setProducts(uniqueProducts)
    }
  }, [user, allProducts, getProductsBySupplier])


  const handleEditProduct = (product) => {
    setEditingProduct(product)
    setNewProduct({
      name: product.name,
      price: product.price.toString(),
      quantity: product.quantity,
      unit: product.unit || 'kg',
      description: product.description,
      image: product.image,
      isOrganic: product.isOrganic,
      licenseNumber: product.licenseNumber || '',
      isVerified: product.isVerified,
      category: product.category || 'vegetables'
    })
    setShowAddProduct(true)
  }

  const handleAddProduct = async () => {
    if (!newProduct.name || !newProduct.price || !newProduct.quantity || !newProduct.unit) {
      alert('Please fill all required fields')
      return
    }

    const productData = {
      ...newProduct,
      price: parseFloat(newProduct.price),
      quantity: parseInt(newProduct.quantity),
      stock: parseInt(newProduct.quantity),
      supplierId: user.id,
      supplierName: user.name,
      supplierRating: user.rating || 4.5,
      image: newProduct.image || '🥬',
      category: newProduct.category || 'vegetables'
    }

    const addedProduct = await addProduct(productData)
    console.log('Product added via API:', addedProduct)

    if (addedProduct) {
      // Don't manually update local state - let ProductContext real-time sync handle it
      alert('Product added successfully!')
    } else {
      alert('Failed to add product. Please try again.')
    }

    setNewProduct({
      name: '',
      price: '',
      quantity: '',
      unit: 'kg',
      description: '',
      category: 'vegetables',
      isOrganic: false
    })
    setShowAddProduct(false)
  }

  const handleUpdateProduct = async () => {
    if (!newProduct.name || !newProduct.price || !newProduct.quantity || !newProduct.unit) {
      alert('Please fill all required fields')
      return
    }

    const updatedData = {
      name: newProduct.name,
      price: parseFloat(newProduct.price),
      quantity: parseInt(newProduct.quantity),
      stock: parseInt(newProduct.quantity),
      unit: newProduct.unit,
      description: newProduct.description,
      image: newProduct.image || editingProduct.image,
      isOrganic: newProduct.isOrganic,
      licenseNumber: newProduct.isOrganic ? null : newProduct.licenseNumber,
      isVerified: newProduct.isOrganic ? true : newProduct.isVerified,
      category: newProduct.category
    }

    const updated = await updateProduct(editingProduct.id, updatedData)
    if (updated) {
      // Don't manually update local state - let ProductContext real-time sync handle it
      alert('Product updated successfully!')
    }

    setNewProduct({
      name: '',
      price: '',
      quantity: '',
      description: '',
      image: '',
      isOrganic: false,
      licenseNumber: '',
      isVerified: false,
      category: 'vegetables'
    })
    setShowAddProduct(false)
  }

  const handleDeleteProduct = async (productId) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      const deleted = await deleteProduct(productId)
      if (deleted) {
        // Don't manually update local state - let ProductContext real-time sync handle it
        alert('Product deleted successfully!')
      } else {
        alert('Failed to delete product')
      }
    }
  }

  const verifyFSSAILicense = async (licenseNumber) => {
    // Simulate FSSAI license verification
    // In a real application, this would call the FSSAI API
    return new Promise((resolve) => {
      setTimeout(() => {
        // Mock verification logic - in reality, this would validate against FSSAI database
        const isValid = licenseNumber.length >= 8 && licenseNumber.match(/^[A-Z0-9]+$/)
        resolve({
          isValid,
          message: isValid ? 'License verified successfully' : 'Invalid license number'
        })
      }, 1000)
    })
  }

  const handleProductNameChange = (name) => {
    setNewProduct({ ...newProduct, name })
  }

  const handleOrganicChange = (isOrganic) => {
    setNewProduct({ ...newProduct, isOrganic })
  }

  const handleVerifyLicense = async () => {
    if (!newProduct.licenseNumber.trim()) {
      alert('Please enter a license number')
      return
    }

    try {
      const result = await verifyFSSAILicense(newProduct.licenseNumber)
      if (result.isValid) {
        setNewProduct({ ...newProduct, isVerified: true })
        alert('✅ ' + result.message)
      } else {
        setNewProduct({ ...newProduct, isVerified: false })
        alert('❌ ' + result.message)
      }
    } catch (error) {
      alert('Error verifying license. Please try again.')
    }
  }

  const handleAddFlashSale = async () => {
    if (newFlashSale.product && newFlashSale.newPrice) {
      // Calculate endTime from duration
      const now = new Date()
      const days = parseInt(newFlashSale.durationDays) || 0
      const hours = parseInt(newFlashSale.durationHours) || 0
      const minutes = parseInt(newFlashSale.durationMinutes) || 0
      const endTime = new Date(now.getTime() + days * 24 * 60 * 60 * 1000 + hours * 60 * 60 * 1000 + minutes * 60 * 1000).toISOString()

      // Use uploaded image if available
      const imageUrl = newFlashSale.image

      const flashSaleData = {
        product: newFlashSale.product,
        oldPrice: parseFloat(newFlashSale.oldPrice),
        price: parseFloat(newFlashSale.newPrice),
        discount: parseFloat(newFlashSale.discount),
        total: parseInt(newFlashSale.totalQuantity),
        quantityUnit: newFlashSale.quantityUnit,
        endTime,
        supplier: user.name,
        supplierId: user.id,
        image: imageUrl
      }
      const addedFlashSale = flashSalesDatabase.addFlashSale(flashSaleData)
      setFlashSales([...flashSales, addedFlashSale])
      setNewFlashSale({
        product: '',
        oldPrice: '',
        newPrice: '',
        discount: '',
        totalQuantity: '',
        quantityUnit: 'kg',
        durationDays: '',
        durationHours: '',
        durationMinutes: '',
        image: ''
      })
      setShowAddFlashSale(false)
    } else {
      alert('Please fill in all required fields (product name and new price)')
    }
  }

  const handleEditFlashSale = (sale) => {
    setNewFlashSale({
      product: sale.product,
      oldPrice: sale.oldPrice.toString(),
      newPrice: sale.price.toString(),
      discount: sale.discount.toString(),
      totalQuantity: sale.total.toString(),
      durationHours: '',
      durationMinutes: '',
      image: sale.image || ''
    })
    setEditingFlashSale(sale)
    setShowAddFlashSale(true)
  }

  const handleUpdateFlashSale = async () => {
    if (newFlashSale.product && newFlashSale.newPrice && editingFlashSale) {
      // Calculate endTime from duration
      const now = new Date()
      const hours = parseInt(newFlashSale.durationHours) || 0
      const minutes = parseInt(newFlashSale.durationMinutes) || 0
      const endTime = new Date(now.getTime() + hours * 60 * 60 * 1000 + minutes * 60 * 1000).toISOString()

      // Use uploaded image if available, otherwise keep existing
      const imageUrl = newFlashSale.image || editingFlashSale.image

      const updatedData = {
        product: newFlashSale.product,
        oldPrice: parseFloat(newFlashSale.oldPrice),
        price: parseFloat(newFlashSale.newPrice),
        discount: parseFloat(newFlashSale.discount),
        total: parseInt(newFlashSale.totalQuantity),
        endTime,
        image: imageUrl
      }

      const updatedFlashSale = flashSalesDatabase.updateFlashSale(editingFlashSale.id, updatedData)
      if (updatedFlashSale) {
        setFlashSales(flashSales.map(fs => fs.id === editingFlashSale.id ? updatedFlashSale : fs))
        setEditingFlashSale(null)
        setNewFlashSale({
          product: '',
          oldPrice: '',
          newPrice: '',
          discount: '',
          totalQuantity: '',
          durationHours: '',
          durationMinutes: '',
          image: ''
        })
        setShowAddFlashSale(false)
      }
    } else {
      alert('Please fill in all required fields')
    }
  }

  const handleDeleteFlashSale = (saleId) => {
    if (window.confirm('Are you sure you want to delete this flash sale?')) {
      const success = flashSalesDatabase.deleteFlashSale(saleId)
      if (success) {
        setFlashSales(flashSales.filter(fs => fs.id !== saleId))
        alert('Flash sale deleted successfully')
      } else {
        alert('Failed to delete flash sale')
      }
    }
  }

  const openBargainChat = (bargain) => {
    setSelectedBargain(bargain)
    setBargainMessage('')
    setBargainOffer('')
  }

  const sendBargainMessage = () => {
    if (!bargainOffer) return
    bargainsDatabase.addMessage(selectedBargain.id, {
      sender: 'supplier',
      offer: bargainOffer ? parseFloat(bargainOffer) : undefined,
      message: `Counter offer: ₹${bargainOffer}`
    })
    // Refresh bargain from DB
    const updated = bargainsDatabase.getBargainById(selectedBargain.id)
    setSelectedBargain(updated)
    setBargainOffer('')
  }

  const confirmSupplierBargain = (amount) => {
    if (!amount) {
      alert('Please enter an amount first')
      return
    }

    // Update bargain status to accepted
    bargainsDatabase.updateBargain(selectedBargain.id, { 
      status: 'accepted',
      finalPrice: parseFloat(amount)
    })

    // Add confirmation message
    bargainsDatabase.addMessage(selectedBargain.id, {
      sender: 'supplier',
      offer: parseFloat(amount),
      message: `Deal accepted at ₹${amount}`
    })

    // Refresh and close
    const updated = bargainsDatabase.getBargainById(selectedBargain.id)
    setSelectedBargain(updated)
    alert(`Deal accepted at ₹${amount}`)
    setSelectedBargain(null)
  }

  const handleBargainResponse = (bargainId, response) => {
    setBargains(bargains.map(bargain => 
      bargain.id === bargainId 
        ? { ...bargain, status: response }
        : bargain
    ))
  }

  const isFarmer = user?.userType === 'farmer' || user?.type === 'farmer'

  const stats = {
    totalProducts: products.length,
    activeBargains: bargains.filter(b => b.status === 'pending').length,
    activeFlashSales: flashSales.filter(f => f.status === 'active').length,
    totalRevenue: 25000,
    totalOrders: 45,
    averageRating: reviews.length > 0 
      ? (reviews.reduce((sum, review) => sum + review.rating, 0) / reviews.length).toFixed(1)
      : 0,
    totalReviews: reviews.length
  }

  return (
    <div className="max-w-7xl mx-auto py-8 px-4">
      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <Package className="w-8 h-8 text-blue-600" />
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Total Products</p>
              <p className="text-2xl font-semibold text-gray-900">{stats.totalProducts}</p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <DollarSign className="w-8 h-8 text-green-600" />
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Total Revenue</p>
              <p className="text-2xl font-semibold text-gray-900">₹{stats.totalRevenue}</p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <ShoppingCart className="w-8 h-8 text-orange-600" />
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Total Orders</p>
              <p className="text-2xl font-semibold text-gray-900">{stats.totalOrders}</p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <Star className="w-8 h-8 text-yellow-600" />
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Avg Rating</p>
              <p className="text-2xl font-semibold text-gray-900">{stats.averageRating}</p>
              <p className="text-xs text-gray-500">{stats.totalReviews} reviews</p>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="bg-white rounded-lg shadow mb-8">
        <div className="border-b border-gray-200">
          <nav className="flex space-x-8 px-6">
            {[
              { id: 'overview', name: 'Overview', icon: BarChart3 },
              { id: 'products', name: 'Product Catalog', icon: Package },
              { id: 'bargains', name: 'Bargain Requests', icon: MessageSquare },
              { id: 'flashsales', name: 'Flash Sales', icon: Zap },
              { id: 'reviews', name: 'Reviews', icon: Star },
