                    { name: 'Garlic', price: '₹100/kg', image: '🧄' },
                    { name: 'Coriander Leaves', price: '₹40/kg', image: '🌿' },
                    { name: 'Mint Leaves', price: '₹60/kg', image: '🌱' },
                    { name: 'Lemon', price: '₹80/kg', image: '🍋' }
