import React, { useState } from 'react'
import { MapPin, Map } from 'lucide-react'
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'
import 'leaflet/dist/leaflet.css'

// Fix for default markers in react-leaflet
import L from 'leaflet'
delete L.Icon.Default.prototype._getIconUrl
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
})

const LocationTracker = () => {
  const [location, setLocation] = useState(null)
  const [error, setError] = useState('')
  const [showMap, setShowMap] = useState(false)

  const getLocation = () => {
    if (!navigator.geolocation) {
      setError('Geolocation is not supported by your browser.')
      return
    }

    setError('')
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        const newLocation = {
          lat: pos.coords.latitude,
          lng: pos.coords.longitude
        }
        setLocation(newLocation)
        setShowMap(true)
        setError('')
      },
      (err) => {
        setError('Unable to retrieve your location. Please check your browser permissions.')
        console.error('Geolocation error:', err)
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 300000
      }
    )
  }

  return (
    <div className="card space-y-4">
      <div className="flex items-center gap-2 mb-4">
        <MapPin className="w-5 h-5 text-primary-600" />
        <h3 className="text-lg font-semibold">Location Tracker</h3>
      </div>

      <button 
        className="btn-primary w-full flex items-center justify-center gap-2" 
        onClick={getLocation}
      >
        <Map className="w-4 h-4" />
        Get My Location
      </button>

      {location && (
        <div className="space-y-3">
          <div className="bg-gray-50 p-3 rounded-lg">
            <div className="text-sm font-medium text-gray-700">Your Location:</div>
            <div className="text-sm text-gray-600">Latitude: {location.lat.toFixed(6)}</div>
            <div className="text-sm text-gray-600">Longitude: {location.lng.toFixed(6)}</div>
          </div>

          {showMap && (
            <div className="h-64 rounded-lg overflow-hidden border">
              <MapContainer 
                center={[location.lat, location.lng]} 
                zoom={15} 
                style={{ height: '100%', width: '100%' }}
              >
                <TileLayer
                  url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                  attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                />
                <Marker position={[location.lat, location.lng]}>
                  <Popup>
                    <div>
                      <h3 className="font-bold">Your Location</h3>
                      <p>Lat: {location.lat.toFixed(6)}</p>
                      <p>Lng: {location.lng.toFixed(6)}</p>
                    </div>
                  </Popup>
                </Marker>
              </MapContainer>
            </div>
          )}
        </div>
      )}

      {error && (
        <div className="text-red-600 text-sm bg-red-50 p-3 rounded-lg">
          {error}
        </div>
      )}
    </div>
  )
}

export default LocationTracker

src/components/Rating/ReviewSystem.jsx.content:
import React, { useState, useEffect } from 'react'
import { Star, Upload, X, Camera } from 'lucide-react'
import { useAuth } from '../../contexts/AuthContext'
import { reviewsDatabase } from '../../data/userDatabase'

const ReviewSystem = ({ itemId, itemName, supplierId, supplierName, onClose, onSubmit, readOnly = false }) => {
  const { user } = useAuth()
  const [rating, setRating] = useState(0)
  const [comment, setComment] = useState('')
  const [selectedImage, setSelectedImage] = useState(null)
  const [imagePreview, setImagePreview] = useState(null)
  const [reviews, setReviews] = useState([])
  const [showForm, setShowForm] = useState(false)
  const [loading, setLoading] = useState(false)

  const handleImageUpload = (event) => {
    const file = event.target.files[0]
    if (file) {
      setSelectedImage(file)
      const reader = new FileReader()
      reader.onload = (e) => {
        setImagePreview(e.target.result)
      }
      reader.readAsDataURL(file)
    }
  }

  const removeImage = () => {
    setSelectedImage(null)
    setImagePreview(null)
  }

  // Load existing reviews for this supplier
  useEffect(() => {
    if (supplierId) {
      const existingReviews = reviewsDatabase.getReviewsBySupplier(supplierId)
      setReviews(existingReviews)
    }
  }, [supplierId])

  const handleSubmitReview = () => {
    if (rating === 0) {
      alert('Please select a rating')
      return
    }

    if (!comment.trim()) {
      alert('Please write a comment')
      return
    }

    setLoading(true)

    try {
      const newReview = {
        productId: itemId,
        productName: itemName,
        supplierId: supplierId,
        supplierName: supplierName,
        vendorId: user?.id || 1,
        vendorName: user?.name || 'Anonymous',
        rating,
        comment,
        image: imagePreview,
        date: new Date().toISOString(),
        status: 'published'
      }

      // Save to database
      const savedReview = reviewsDatabase.addReview(newReview)
      console.log('Review submitted successfully:', savedReview)

      // Update local state
      setReviews(prev => [...prev, savedReview])
      setRating(0)
      setComment('')
      setSelectedImage(null)
      setImagePreview(null)
      setShowForm(false)

      if (onSubmit) {
        onSubmit(savedReview)
      }

      // Close the modal after successful submission
      if (onClose) {
        onClose()
      }
    } catch (error) {
      console.error('Error submitting review:', error)
      alert('Failed to submit review. Please try again.')
    } finally {
      setLoading(false)
    }
  }

  const averageRating = reviews.length > 0 
    ? (reviews.reduce((sum, review) => sum + review.rating, 0) / reviews.length).toFixed(1)
    : 0

  return (
    <div className="bg-white rounded-lg shadow-lg max-w-md w-full">
      <div className="p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold">Reviews for {itemName}</h3>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        {/* Average Rating */}
        <div className="flex items-center gap-2 mb-4">
          <div className="flex items-center">
            {[1, 2, 3, 4, 5].map((star) => (
              <Star
                key={star}
                className={`w-5 h-5 ${
                  star <= averageRating
                    ? 'text-yellow-400 fill-current'
                    : 'text-gray-300'
                }`}
              />
            ))}
          </div>
          <span className="text-sm text-gray-600">
            {averageRating} ({reviews.length} reviews)
          </span>
        </div>

        {/* Write Review Button - Only show if not read-only */}
        {!readOnly && !showForm && (
          <button
            onClick={() => setShowForm(true)}
            className="w-full bg-primary-600 hover:bg-primary-700 text-white py-2 px-4 rounded-lg transition-colors mb-4"
          >
            Write a Review
          </button>
        )}

        {/* Review Form - Only show if not read-only */}
        {!readOnly && showForm && (
          <div className="border rounded-lg p-4 mb-4">
            <h4 className="font-medium mb-3">Write Your Review</h4>

            {/* Rating Selection */}
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Rating *
              </label>
              <div className="flex items-center gap-1">
                {[1, 2, 3, 4, 5].map((star) => (
                  <button
                    key={star}
                    onClick={() => setRating(star)}
                    className="focus:outline-none"
                  >
                    <Star
                      className={`w-6 h-6 ${
                        star <= rating
                          ? 'text-yellow-400 fill-current'
                          : 'text-gray-300'
                      }`}
                    />
                  </button>
                ))}
              </div>
            </div>

            {/* Comment */}
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Comment *
              </label>
              <textarea
                value={comment}
                onChange={(e) => setComment(e.target.value)}
                placeholder="Share your experience..."
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                rows="3"
              />
            </div>

            {/* Image Upload */}
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Add Photo (Optional)
              </label>
              <div className="border-2 border-dashed border-gray-300 rounded-lg p-4 text-center">
                {!imagePreview ? (
                  <div>
                    <Camera className="w-8 h-8 text-gray-400 mx-auto mb-2" />
                    <input
                      type="file"
                      accept="image/*"
                      onChange={handleImageUpload}
                      className="hidden"
                      id="image-upload"
                    />
                    <label
                      htmlFor="image-upload"
                      className="cursor-pointer text-primary-600 hover:text-primary-700"
                    >
                      Click to upload image
                    </label>
                  </div>
                ) : (
                  <div className="relative">
                    <img
                      src={imagePreview}
                      alt="Preview"
                      className="w-full h-32 object-cover rounded-lg"
                    />
                    <button
                      onClick={removeImage}
                      className="absolute top-2 right-2 bg-red-500 text-white rounded-full p-1 hover:bg-red-600"
                    >
                      <X className="w-4 h-4" />
                    </button>
                  </div>
                )}
              </div>
            </div>

            {/* Submit Button */}
            <div className="flex gap-2">
              <button
                onClick={handleSubmitReview}
                disabled={loading}
                className="w-full bg-primary-600 hover:bg-primary-700 disabled:bg-gray-400 text-white py-2 px-4 rounded-lg transition-colors"
              >
                {loading ? 'Submitting...' : 'Submit Review'}
              </button>
            </div>
          </div>
        )}

        {/* Reviews List */}
        <div className="space-y-4 max-h-64 overflow-y-auto">
          {reviews.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <p>No reviews yet for this supplier.</p>
              <p className="text-sm">Be the first to share your experience!</p>
            </div>
          ) : (
            <>
              {/* Past Reviews Header */}
              <div className="flex items-center justify-between mb-4">
                <h4 className="font-medium text-gray-900">Past Reviews ({reviews.length})</h4>
              </div>

              {reviews.map((review) => (
                <div key={review.id} className="border-b pb-4">
                  <div className="flex items-center gap-2 mb-2">
                    <div className="flex items-center">
                      {[1, 2, 3, 4, 5].map((star) => (
                        <Star
                          key={star}
                          className={`w-4 h-4 ${
                            star <= review.rating
                              ? 'text-yellow-400 fill-current'
                              : 'text-gray-300'
                          }`}
                        />
                      ))}
                    </div>
                    <span className="text-sm text-gray-500 font-medium">{review.vendorName || 'Anonymous'}</span>
                    <span className="text-sm text-gray-500">
                      {new Date(review.date).toLocaleDateString()}
                    </span>
                  </div>
                  <p className="text-sm text-gray-700 mb-2">{review.comment}</p>
                  {review.image && (
                    <img
                      src={review.image}
                      alt="Review"
                      className="w-20 h-20 object-cover rounded-lg"
                    />
                  )}
                </div>
              ))}
            </>
          )}
        </div>
      </div>
    </div>
  )
}

export default ReviewSystem

