          }
        }
      )
      const data = await response.json()

      if (data && data.address) {
        const {
          suburb,
          neighbourhood,
          village,
          town,
          city_district,
          city,
          state_district
        } = data.address
        return (
          suburb ||
          neighbourhood ||
          village ||
          town ||
          city_district ||
          city ||
          state_district ||
          'Unknown Area'
        )
      }
      return 'Unknown Area'
    } catch (error) {
      console.error('Error getting area name:', error)
      return 'Unknown Area'
    }
  }

